import logo from "../images/divumlogo.png";
import "../App.css";
import { useEffect, useState } from "react";
import axios from "axios";

import { Link, useNavigate } from "react-router-dom";

function Records() {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:3002/getUsers")
      .then((users) => {
        setUsers(users.data);
      })
      .catch((err) => console.log(err));
  }, []);

  //   const getAllUser = () => {
  //     fetch("http://localhost:3002/getUsers")
  //       .then((users) => {
  //         setUsers(users.data);
  //       })
  //       .catch((err) => console.log(err));
  //   };

  const navigate = useNavigate;

  const addSubmit = async (e) => {
    e.preventDefault();
    navigate("/");
  };

  const deleteUser = (id, name) => {
    if (window.confirm(`Are you sure you want to delete ${name}`)) {
      axios.get("http://localhost:3002/deleteUser")
        .then((res) => res.json())
        .then((data) => {
          alert(data.data);
          // getAllUser();
        });
    } else {
    }
  };



  return (
    <>
      <div className="logo ">
        <img src={logo} alt="logo" className="logo" />
        <Link to="/">
          <button className="addbtn btn" onSubmit={addSubmit}>
            Add
          </button>
        </Link>
      </div>

      <section>
        <div className="w-100 vh-100 d-flex justify-content-center align-items-center">
          <div className="dw-50">
            <table className="table ">
              <thead>
                <tr>
                  <th>Email</th>
                  <th>FirstName</th>
                  <th>LastName</th>
                  <th>Phone_no</th>
                  <th>Date of Birth</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => {
                  return (
                    <tr>
                      <td>{user.email}</td>
                      <td>{user.firstname}</td>
                      <td>{user.lastname}</td>
                      <td>{user.mobile_no}</td>
                      <td>{user.dob.slice(0, 10)}</td>
                      <td>
                        <button className="btn edit">Edit</button>

                        <button
                          className="btn delete"
                          onClick={() => {
                            deleteUser(user._id, user.firstname);
                          }}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </section>
    </>
  );
}

export default Records;
