import logo from "../images/divumlogo.png";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import {fnameValidate,lnameValidate} from '../Validate';

function AddUser() {
  const [email, setEmail] = useState("");
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [mobile_no, setMobile_no] = useState("");
  const [dob, setDob] = useState("");
  const [address, setAddress] = useState("");

  const navigate = useNavigate();

  const handleOnSubmit = async (e) => {

    e.preventDefault(); 


    let result = await fetch("http://localhost:3002/getUsers", {
      method: "post",
      body: JSON.stringify({
        email,
        firstname,
        lastname,
        mobile_no,
        dob,
        address,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    result = await result.json();
    console.warn(result);

    navigate("/getUsers");

    if (result) {
      alert("Data saved succesfully");
      setEmail("");
      setFirstname("");
      setLastname("");
      setMobile_no("");
      setDob("");
      setAddress("");
    }
  };
//formvalidation starts
  const formValidate = async (id) =>
{

  const elementName =id;
  const field = document.querySelector(`#${elementName}`);

  //regex constants
  const alphaExp = /^[a-zA-Z]+$/;
  const exp=/^[0-9]+$/;

   switch(elementName)
  {

    case 'fname':

     // console.log(field);
      if(field.value==='')
      {
        setError(field,'*FirstName is required')
      }
      else if (!field.value.match(alphaExp))
      {
        setError(field,"*Enter only Aphabets")
      }
      else
      {
        setSuccess(field)
      }
      break;
    case 'lname':
      if(field.value==='')
      {
        setError(field,'*LastName is required')
      }
      else if (!field.value.match(alphaExp))
      {
        setError(field,"*Enter only Aphabets")
      }
      else
      {
        setSuccess(field)
      }
      break;
      case 'mobile':
        if(field.value==='')
      {
        setError(field,'*Mobile Number is required')
      }
      else if (!field.value.match(exp))
      {
        setError(field,'*Enter only Numbers')
      }
      else if (!field.value.length ===10)
      {
        setError(field,'*Enter a Valid Number')
      }
      else
      {
        setSuccess(field)
      }
        break;
      case 'email':
        break;
      case 'address':
        break;

    default:
      alert("error");

  }

};

//setting error msg 
const setError = (field,msg) => {
  console.log(field)
  const inputGroup = field.parentElement;
  console.log(inputGroup)
  const errorElement = inputGroup.querySelector('.errormsg')
  console.log(errorElement)
 errorElement.innerText =msg;

}
//setting success 
function setSuccess(field){
  const inputGroup = field.parentElement;
  const errorElement = inputGroup.querySelector('.errormsg')
  errorElement.innerText = '';
}

  return (
    <>
      <div className="logo ">
        <img src={logo} alt="logo" className="logo" />
      </div>
      <section>
        <div className="container">
          <form id="form">
            <div className="row100">
              <div className="col">
                <div className="inputBox error">
                  <input
                    type="text"
                    value={firstname}
                    id="fname"
                    name="fname"
                    onChange={(e) => setFirstname(e.target.value) }
                      onBlur={(e) => formValidate(e.target.id)}
                  />
                  <span className="text">First Name</span>
                  <span className="line"></span>
                  <span className="errormsg"></span>
                </div>
              </div>
              <div className="col">
                <div className="inputBox">
                  <input
                    type="text"
                    value={lastname}
                    id="lname"
                    name="lname"
                    onChange={(e) => setLastname(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}
                  />
                  <span className="text">Last Name</span>
                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
            </div>
            <div className="row100">
              <div className="col">
                <div className="inputBox">
                  <input
                    type="email"
                    value={email}
                    id="email"
                    name="email"
                    onChange={(e) => setEmail(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

                  />
                  <span className="text">Email</span>
                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
              <div className="col">
                <div className="inputBox">
                  <input
                    type="date"
                    value={dob}
                    id="dob"
                    name="dob"
                    onChange={(e) => setDob(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

                  />
                  <span className="text">Date Of Birth</span>
                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
              <div className="col">
                <div className="inputBox">
                  <input
                    type="text"
                    value={mobile_no}
                    id="mobile"
                    name="mobile"
                    onChange={(e) => setMobile_no(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

                    maxLength="10"
                  />
                  <span className="text">Mobile</span>
                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
            </div>

            <div className="row100">
              <div className="col">
                <div className="inputBox textarea">
                  <textarea
                    value={address}
                    id="address"
                    name="address"
                    maxLength="50"
                    onChange={(e) => setAddress(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

                  ></textarea>
                  <span className="text">Address...</span>
                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
            </div>

            <div className="row100">
              <div className="col">
                <Link to="/getUsers">
                  <input
                    type="submit"
                    value="Submit"
                    onClick={handleOnSubmit}
                  />
                </Link>
              </div>
            </div>
          </form>
        </div>
      </section>
    </>
  );
}

export default AddUser;
