import validator from "validator";

const formValidate = async (id) => {

    const elementName = id;
    const field = document.querySelector(`#${elementName}`);

    //regex constants
    const alphaExp = /^[a-zA-Z]+$/;
    const exp = /^[0-9]+$/;
    const addressExp = /^[a-zA-Z0-9-/]+$/;
    const emailExp = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    const success=false;

    switch (elementName) {

      case 'fname':

        // console.log(field);
        if (field.value === '') {
          setError(field, '*FirstName is required')
          success=false;
        }
        else if (!field.value.match(alphaExp)) {
          setError(field, "*Enter only Aphabets")
          success=false;
        }
        else {
          setSuccess(field)
          success=true;
        }
        break;
      case 'lname':
        if (field.value === '') {
          setError(field, '*LastName is required')
          success=false;
        }
        else if (!field.value.match(alphaExp)) {
          setError(field, "*Enter only Aphabets")
          success=false;
        }
        else {
          setSuccess(field)
          success=true;
        }
        break;
      case 'mobile':
        if (field.value === '') {
          setError(field, '*Mobile Number is required')
          success=false;
        }
        else if (!field.value.match(exp)) {
          setError(field, '*Enter only Numbers')
          success=false;
        }
        else if (field.value.length < 10) {
          setError(field, '*Enter a Valid Number')
          success=false;
        }
        else {
          setSuccess(field)
          success=true;
        }
        break;
      case 'email':
        if (field.value === '') {
          setError(field, '*Email is required')
          success=false;
        }
        else if (!field.value.match(emailExp)) {
          setError(field, "*Enter a Valid Email")
          success=false;
        }
        else {
          setSuccess(field)
          success=true;
        }
        break;
      case 'dob':
        const currentDate = new Date();
        const day = currentDate.getDate();
        console.log(day, "_-------------", currentDate, field.value)
        if (field.value === '') {
          setError(field, '*Date is required')
          success=false;
        }
        else if (validator.isDate(field.value) && (field.value < currentDate)) {
          setError(field, 'Enter a Valid Date');
          success=false;
        }
        else {
          setSuccess(field)
        }
        break;
      case 'address':
        if (field.value === '') {
          setError(field, '*Address is required')
          success=false;
        }
        else if (!field.value.match(addressExp)) {
          setError(field, '*Enter a Valid Address')
          success=false;
        }
        else {
          setSuccess(field)
        }
        break;

      default:
        alert("error");

    }
    return true;

  };

  const setError = (field, msg) => {
    const inputGroup = field.parentElement;
    const errorElement = inputGroup.querySelector('.errormsg')
    errorElement.innerText = msg;

  }
  //setting success 
  function setSuccess(field) {
    const inputGroup = field.parentElement;
    const errorElement = inputGroup.querySelector('.errormsg')
    errorElement.innerText = '';
  }
export default formValidate;
