import logo from '../images/divumlogo.png';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const form = document.querySelector('#form')
const email = document.querySelector('#email');
const fullname = document.querySelector('#fname');
const lastname = document.querySelector('#lname');
const mobile = document.querySelector('#mobile');
const dob = document.querySelector('#dob');


console.log(fullname.value)
const fullnameVal = fullname.value;

console.log(lastname.value)

console.log(mobile.value)
console.log(email.value)
console.log(dob.value)




function setError(element,message){
    const inputGroup = element.parentElement;
    const errorElement = inputGroup.querySelector('.errormsg')

    errorElement.innerText = message;
    inputGroup.classList.add('error')
    inputGroup.classList.remove('success')
}

function setSuccess(element){
    const inputGroup = element.parentElement;
    console.log(inputGroup)
    const errorElement = inputGroup.querySelector('.errormsg')

    errorElement.innerText = '';
    inputGroup.classList.add('success')
    inputGroup.classList.remove('error')
}

function AddUser() {


    const [email, setEmail] = useState("");
    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [mobile_no, setMobile_no] = useState("");
    const [dob, setDob] = useState("");
    const [address, setAddress] = useState("");

    const navigate = useNavigate();

    const handleOnSubmit = async (e) => {



        e.preventDefault();


        let result = await fetch(
            'http://localhost:3002/getUsers',
            {
                method: "post",
                body: JSON.stringify({ email, firstname, lastname, mobile_no, dob, address }),
                headers:
                {
                    'Content-Type': 'application/json'
                }
            })
        result = await result.json();
        console.warn(result);

        navigate('/getUsers')

        if (result) {
            alert("Data saved succesfully");
            setEmail("");
            setFirstname("");
            setLastname("");
            setMobile_no("");
            setDob("");
            setAddress("");
        }


    }


    function valfname()
{
    alert(firstname)

        var alphaExp = /^[a-zA-Z]+$/;
    alert({fullnameVal})
    if(fullnameVal.match(alphaExp))
    {

        setSuccess(fullname)

    }
    else
    {
        setError(fullname,"Enter only alphabets");

    }
}

    return (
        <>

            <div className='logo '>
                <img src={logo} alt='logo' className='logo' />
            </div>
            <section>


                <div className="container">
                    <form id='form'>
                        <div className="row100">
                            <div className="col">
                                <div className="inputBox error">
                                    <input type="text" value={firstname} id='fname' name='fname' onChange={(e) => setFirstname(e.target.value)} onBlur={valfname}/>
                                    <span className="text">First Name</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>

                                </div>
                            </div>
                            <div className="col">
                                <div className="inputBox">
                                    <input type="text" value={lastname} id='lname' name='lname' onChange={(e) => setLastname(e.target.value)}  />
                                    <span className="text">Last Name</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>

                                </div>
                            </div>
                        </div>
                        <div className="row100">
                            <div className="col">
                                <div className="inputBox">
                                    <input type="email" value={email} id='email' name='email' onChange={(e) => setEmail(e.target.value)}  />
                                    <span className="text">Email</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>

                                </div>
                            </div>
                            <div className="col">
                                <div className="inputBox">
                                    <input type="date" value={dob} id='dob' name='dob' onChange={(e) => setDob(e.target.value)} />
                                    <span className="text">Date Of Birth</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>

                                </div>
                            </div>
                            <div className="col">
                                <div className="inputBox">
                                    <input type="text" value={mobile_no} id='mobile' name='mobile' onChange={(e) => setMobile_no(e.target.value)} maxLength='10' />
                                    <span className="text">Mobile</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>

                                </div>
                            </div>
                        </div>



                        <div className="row100">
                            <div className="col">
                                <div className="inputBox textarea">
                                    <textarea value={address} id='address' name='address' maxLength='50'
                                        onChange={(e) => setAddress(e.target.value)} ></textarea>
                                    <span className="text">Address...</span>
                                    <span className="line"></span>
                                    <div className="errormsg"></div>
                                </div>
                            </div>
                        </div>

                        <div className="row100">
                            <div className="col">

                                <Link to='/getUsers' >
                                    <input type="submit" value="Submit" onClick={handleOnSubmit} />

                                </Link>

                            </div>
                        </div>
                    </form>
                </div>

            </section>

        </>
    )
}

export default AddUser
