                  <span className="line"></span>
                  <div className="errormsg"></div>
                </div>
              </div>
            </div>
            <div className="row100">
              <div className="col">
                <div className="inputBox">
                  <input
                    type="text"
                    value={email}
                    id="email"
                    name="email"
                    onChange={(e) => setEmail(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

