                    value={address}
                    id="address"
                    name="address"
                    maxLength="50"
                    onChange={(e) => setAddress(e.target.value)}
                    onBlur={(e) => formValidate(e.target.id)}

                  ></textarea>
                  <span className="text">Address...</span>
                  <span className="line"></span>

                  <div style={{ position: 'relative', top: '88px' }}
                    className="errormsg"></div>
                </div>
              </div>
            </div>

            <div className="row100">
              <div className="col">
                <Link to="/getUsers">
                  <input
                    type="submit"
                    value="Submit"
                    onClick={handleOnSubmit}
                  />
                </Link>
              </div>
            </div>
          </form>
        </div>
      </section>
    </>
  );
}

export default AddUser;
